import React,{useEffect, useState} from 'react'
import {getUserList} from '@/services/index'
import { useRequest } from 'ahooks'
import { Space, Table, Switch, Image } from 'antd'
import type { TableProps } from 'antd'
import type { UserListItem } from '@/services/types'


const UserList = () => {
  const [query, setQuery] = useState({
    page: 1,
    pagesize: 5
  }) 

  const { data: listRes, error, loading, run } = useRequest(getUserList, {
    refreshOnWindowFocus: true,
    defaultParams: [query]
  })

  useEffect(() => {
    // 手动触发接口执行
    run(query)
  }, [query])
  
  const columns: TableProps<UserListItem>['columns'] = [
    {
      title: '用户名',
      dataIndex: 'username',
      key: 'username',
      fixed: 'start',
      width: 150,
      align: 'center'
    },
    {
      title: '账号状态',
      dataIndex: 'status',
      key: 'status',
      width: 100,
      align: 'center',
      render: (_, record) => (
        <Switch
          checkedChildren="启用"
          unCheckedChildren="禁用"
          checked={record.status === 1}
          onChange={checked => {
            
          }}
        />
      )
    },
    {
      title: '头像',
      dataIndex: 'avator',
      key: 'avator',
      width: 150,
      align: 'center',
      render: (_, record) => <Image style={{width:'30px',borderRadius:'15px'}} src={record.avator} fallback="data:image/png;base64,"/>
    },
    {
      title: '年龄',
      dataIndex: 'age',
      key: 'age',
      width: 100,
      align: 'center',
      render: _ => _ ?? '--'
    },
    {
      title: '性别',
      dataIndex: 'sex',
      key: 'sex',
      width: 100,
      align: 'center',
      render: _ => _ ?? '--'
    },
    {
      title: '邮箱',
      dataIndex: 'email',
      key: 'email',
      width: 250,
      align: 'center',
      render: _ => _ ?? '--'
    },
    {
      title: '密码',
      dataIndex: 'password',
      key: 'password',
      width: 150,
      align: 'center',
    },
    {
      title: '最近登陆时间',
      dataIndex: 'lastOnlineTime',
      key: 'lastOnlineTime',
      width: 200,
      align: 'center',
      render: _ => _ ?? '--'
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
      width: 150
    },
    {
      title: '操作',
      key: 'action',
      fixed: 'end',
      align: 'center',
      width: 200,
      render: (_, record) => (
        <Space size="middle">
          <a>编辑</a>
          <a>删除</a>
        </Space>
      ),
    },
  ]


  return (
    <div>
      <Table<UserListItem>
        loading={loading}
        scroll={{ x: 1200 }}
        columns={columns}
        dataSource={listRes?.data.list}
        rowKey={row => row._id}
        size="small"
        pagination={{
          current: query.page,
          pageSize: query.pagesize,
          total: listRes?.data.total,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: total => `共 ${total} 条`,
          pageSizeOptions: [5, 10, 15, 20],
          onChange: (page, pagesize) => {
            setQuery({
              page,
              pagesize
            })
          },
          locale: {
            jump_to: "跳至", // 前缀文字
            page: "页",       // 输入框后缀文字
            items_per_page: "条/页"
          }
        }}
      />
    </div>
  )
}

export default UserList